import React, { Component } from 'react'
import { connect } from 'react-redux'

class Dashboard extends Component {
    render() {

        return (
            <div className='dashboard'>
                <h3 className='dashboard-title'> Your Questions</h3>
                <nav className='dashboard-nav-bar'>
                    <span style={{
                        backgroundColor: !toggleAnswered ? 'cyan' : 'grey',
                        color: !toggleAnswered ? 'orchid' : 'black'
                    }}
                        onClick={this.toggleUnanswered}
                        id='unanswered-nav'
                        className="dashboard-nav">

                        Unanswered Questions
          </span>
                    <span style={{
                        backgroundColor: toggleAnswered ? 'cyan' : 'grey',
                        color: toggleAnswered ? 'orchid' : 'black'
                    }}
                        onClick={this.toggleAnswered}
                        id='answered-nav'
                        className="dashboard-nav">

                        Answered Questions
          </span>
                </nav>
                {toggleAnswered ?
                    <ul className='dashboard-list'>
                        {this.props.answeredQuestionIds.map((id) => (
                            <li key={id} className="question-card-container">
                                <QuestionCard id={id} />
                            </li>
                        ))}
                    </ul> :
                    <ul className='dashboard-list'>
                        {this.props.unansweredQuestionIds.map((id) => (
                            <li key={id} className="question-card-container">
                                <QuestionCard id={id} />
                            </li>
                        ))}
                    </ul>
                }

              </div>
            )
    }

}
function mapStateToProps({ users }) {
    return {

        userIds: Object.keys(users)
            .sort((a, b) => users[b].timestamp - users[a].timestamp )
    }

}

export default connect(mapStateToProps) (Dashboard)
