import React, { Component } from 'react'
import { connect } from 'react-redux'
import Pollresults from '../components/Pollresults'

class Dashboard extends Component {
    state = {
        answered: false,
    }
    answered = (e) => {
        e.preventDefault();

        if (!this.state.answered) {

            this.setState(() => ({
                answered: !this.state.answered,
            }))
        }
    }

    unanswered = (e) => {
        e.preventDefault();


        if (this.state.answered) {
            this.setState(() => ({
                answered: !this.state.answered,
            }))
        }
    }
    render() {
        const { answered } = this.state;
        return (
            <div className='dashboard'>
                <h3 className='dashboard-title'> Your Questions</h3>
                <nav className='dashboard-nav-bar'>
                    <span style={{
                        backgroundColor: !answered ? 'lightgray' : '',
                        color: !answered ? 'white' : 'black'
                    }}
                        onClick={this.unanswered}
                        id='unanswered-nav'
                        className="dashboard-nav">

                        Unanswered Questions
          </span>
                    <span style={{
                        backgroundColor: answered ? 'lightgray' : '',
                        color: answered ? 'white' : 'black'
                    }}
                        onClick={this.answered}
                        id='answered-nav'
                        className="dashboard-nav">

                        Answered Questions
          </span>
                </nav>
                <table className="MyClassName">
                    <thead>
                        <tr>
                            {this.props.titles.map(title =>
                                <th key={title}>{title}</th>
                            )}
                        </tr>
                    </thead>
                    <tbody>
                        {this.props.answeredQuestionIds.map((row,id) =>                        
                            <tr key={id}>
                                {row.map((col, j) =>
                                    <td key={j}>{col}
                                        <Pollresults id={id} />
                                    </td>
                                )}
                            </tr>
                        )}
                    </tbody>
                         </table>
                {/*{answered ?*/}
                {/*    <ul className='dashboard-list'>*/}
                {/*        {this.props.answeredQuestionIds.map((id) => (*/}
                {/*            <li key={id} className="question-card-container">*/}
                {/*                <Pollresults id={id} />*/}
                {/*            </li>*/}
                {/*        ))}*/}
                {/*    </ul> :*/}
                {/*    <ul className='dashboard-list'>*/}
                {/*        {this.props.unansweredQuestionIds.map((id) => (*/}
                {/*            <li key={id} className="question-card-container">*/}
                {/*                <Pollresults id={id} />*/}
                {/*            </li>*/}
                {/*        ))}*/}
                {/*    </ul>*/}
                {/*}*/}

              </div>
            )
    }

}
function mapStateToProps({ questions, authedUser, users }) {


    //sort by timestamp
    const questionIds = Object.keys(questions)
        .sort((a, b) => questions[b].timestamp - questions[a].timestamp);

    const authedUserAnswers = Object.keys(users[authedUser].answers);

    const answeredQuestionIds = [];

    const unansweredQuestionIds = [];


    //sorts questions into answered and unanswered arrays
    questionIds.map((id) => {
        if (authedUserAnswers.includes(id)) {
            answeredQuestionIds.push(id);
            return id;
        }
        else {
            unansweredQuestionIds.push(id);
            return id;
        }
    })


    return {
        questionIds: Object.keys(questions)
            .sort((a, b) => questions[b].timestamp - questions[a].timestamp),
        answeredQuestionIds,
        unansweredQuestionIds,

    }
}
export default connect(mapStateToProps) (Dashboard)
